import React, {useState, useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Modal, ModalBody, ModalFolder, ModalHeader, ModalFooter} from 'reactstrap';
import axios from 'axios';

function App() {

  const baseUrl = "http://localhost/apiphp/";
  const[data, setData] = useState([]);
  const [modalInsert, setModalInsert] = useState(false);

  const opencloseInsertModal=()=>{
    setModalInsert(!modalInsert);
  }

  const peticionGet = async() => {
    await axios.get(baseUrl)
    .then(response=>{
      setData(response.data);
    })
  }

  useEffect(()=>{
    peticionGet();
  },[])


  return (
    <div>
      <br />
      <button className="btn btn-succes" onClick={()=>opencloseInsertModal()}>Insert New Movie</button>
      <br /><br />
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nombre</th>
            <th>Year</th>
            <th>Director</th>
          </tr>
        </thead>
        <tbody>
          {data.map(movie=>(
              <tr key={movie.id}>
                <td>{movie.id}</td>
                <td>{movie.name}</td>
                <td>{movie.year}</td>
                <td>{movie.director}</td>
                <td>
                  <button className="btn btn-primary">Editar</button> {" "}
                  <button className="btn btn-danger">Eliminar</button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>

      <Modal>
        <ModalHeader>
          New Movie
        </ModalHeader>
        <ModalBody>
          <label>Name </label><br />
            <input type="text" className="form-control" name="name" />
          <label>Year </label><br />
            <input type="text" className="form-control" name="year" />
          <label>Director </label><br />
            <input type="text" className="form-control" name="director" />
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary">Insert</button>
          <button className="btn btn-danger">Cancel</button>
        </ModalFooter>

      </Modal>
    </div>
  );
}

export default App;
